import React, { useState } from 'react';
import styles from './myStyles.module.css';

function App() {
  const [angka1, setAngka1] = useState('');
  const [angka2, setAngka2] = useState('');
  const [operasi, setOperasi] = useState('+');
  const [hasil, setHasil] = useState('');

  const hitungHasil = () => {
    const num1 = parseFloat(angka1);
    const num2 = parseFloat(angka2);

    let result;
    switch (operasi) {
      case '+':
        result = num1 + num2;
        break;
      case '-':
        result = num1 - num2;
        break;
      case '*':
        result = num1 * num2;
        break;
      case '/':
        result = num1 / num2;
        break;
      default:
        result = '';
    }

    setHasil(result);
  };

  return (
    <div>
      <input
        type="number"
        id="angka1"
        value={angka1}
        onChange={(e) => setAngka1(e.target.value)}
      />
      <select
        id="operasi"
        value={operasi}
        onChange={(e) => setOperasi(e.target.value)}
      >
        <option value="+">+</option>
        <option value="-">-</option>
        <option value="*">*</option>
        <option value="/">/</option>
      </select>
      <input
        type="number"
        id="angka2"
        value={angka2}
        onChange={(e) => setAngka2(e.target.value)}
      />
      <button id="hitung" onClick={hitungHasil}>
        Hitung
      </button>
      <div id="hasil">{hasil}</div>
    </div>
  );
}

export default App;
