const inputAngka1 = document.getElementById("angka1");
const inputAngka2 = document.getElementById("angka2");
const selectOperasi = document.getElementById("operasi");
const outputHasil = document.getElementById("hasil");

function hitungHasil() {
  const angka1 = parseFloat(inputAngka1.value);
  const angka2 = parseFloat(inputAngka2.value);
  const operasi = selectOperasi.value;

  let hasil;
  switch (operasi) {
    case "+":
      hasil = angka1 + angka2;
      break;
    case "-":
      hasil = angka1 - angka2;
      break;
    case "*":
      hasil = angka1 * angka2;
      break;
    case "/":
      hasil = angka1 / angka2;
      break;
  }

  outputHasil.textContent = hasil;
}

document.getElementById("hitung").addEventListener("click", hitungHasil);
inputAngka1.addEventListener("change", hitungHasil);
inputAngka2.addEventListener("change", hitungHasil);
selectOperasi.addEventListener("change", hitungHasil);

// Menampilkan hasil awal
hitungHasil();
